import React, { useEffect, useState } from 'react'
import Nav from './Nav'
import styled, { keyframes, withTheme } from 'styled-components'

const VillagerDiv = styled.div`
width: 60%;
display: flex;
flex-direction: column;
`

const LoadingBeachballEffect = keyframes`
     from {
         transform: rotate(0deg)
             }
     to {
         transform: rotate(360deg)
    }
`

const LoadingBeachball = styled.img`
animation: ${LoadingBeachballEffect} 2s infinite;
width: 30%;
height: 30%;
`

const LoadingMessage = styled.div`
display: flex;
align-items: center;
justify-content: center;
`


export default function VillagerDetail(props) {
    if (!props.location.state) {
        return (
            <LoadingMessage>
                <LoadingBeachball src="https://i.imgur.com/lnrCkrf.png" />
            </LoadingMessage>
        )
    }
    else {
        const villager = props.location.state.villager.villager
        let backgroundColor = villager['bubble-color']
        let bubbleColor = backgroundColor;
        if (backgroundColor === '#ffffff') {
            backgroundColor = '#000000'
        }
        const bubbleStyle = {
            backgroundColor: backgroundColor,
            color: backgroundColor,
        }
        const styleLight = {
            backgroundColor: '#ffffff',
            borderRadius: '20%',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
        }
        const styleGroup = {
            backgroundColor: '#ffffff',
            display: 'flex',
            flexDirection: 'row'
        }
        return (
            <VillagerDiv
                style={bubbleStyle}
            >
                <Nav />
                <img src={villager['image_uri']} alt={villager.name['name-USen']} />
                <div style={styleGroup}>
                    <h1 style={styleLight}>{villager.name['name-USen']}</h1>
                    <h2 style={styleLight}>{villager.species}</h2>
                </div>
                <h3 style={styleLight}>Birthday: {villager['birthday-string']}</h3>
                <h3 style={styleLight}>Personality: {villager.personality}</h3>
                <h3 style={styleLight}>Catchphrase: '{villager['catch-translations']['catch-USen']}'</h3>
                <h4 style={styleLight}>Hobby: {villager.hobby}</h4>
                <div style={styleLight}>
                    <h4>Quote:</h4>
                    <p>"{villager.saying}"</p>
                </div>
            </VillagerDiv>
        )
    }
}
