import React from 'react'
import { Link } from 'react-router-dom'
import styled from 'styled-components'

const ThumbnailDiv = styled.div`
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
border: 1px solid transparent;
border-radius: 50%;
height: 100%;
width: 180px;
padding: 5px;
margin: 5px;
color: white;
`

export default function Thumbnail(props) {
    const villager = props.villager
    const bubbleColor = villager['bubble-color']
    console.log(bubbleColor)
    const bubbleStyle = {
        backgroundColor: bubbleColor,
    }
    return (
        <Link to={{
            pathname: `/${villager.id}`,
            state: {
                villager: { villager },
                homepageVillagers: props.thumbnailVillagers
            }
        }}>
            <ThumbnailDiv style={bubbleStyle}>
                <img src={villager['icon_uri']} alt={villager.name['name-USen']} />
                <h3>{villager.name['name-USen']}</h3>
            </ThumbnailDiv>
        </Link>
    )
}
