import React, { useEffect, useState } from 'react'
import Nav from './Nav'
import styled, { keyframes, withTheme } from 'styled-components'

const ContainerDiv = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`

const VillagerDiv = styled.div`
margin-top: 20px;
width: 100%;
display: flex;
flex-direction: column;
align-items: center;
`

const VillagerImg = styled.img`
width: 80%;
margin-top: 10%;
`

const LoadingBeachballEffect = keyframes`
     from {
         transform: rotate(0deg)
             }
     to {
         transform: rotate(360deg)
    }
`

const LoadingBeachball = styled.img`
animation: ${LoadingBeachballEffect} 2s infinite;
width: 30%;
height: 30%;
`

const LoadingMessage = styled.div`
display: flex;
align-items: center;
justify-content: center;
`


export default function VillagerDetail(props) {
    if (!props.location.state) {
        return (
            <LoadingMessage>
                <LoadingBeachball src="https://i.imgur.com/lnrCkrf.png" />
            </LoadingMessage>
        )
    }
    else {
        const villager = props.location.state.villager.villager
        let backgroundColor = villager['bubble-color']
        let bubbleColor = backgroundColor;
        if (backgroundColor === '#ffffff') {
            backgroundColor = '#000000'
        }
        const bubbleStyle = {
            backgroundColor: backgroundColor,
            color: backgroundColor,
        }
        const styleLight = {
            backgroundColor: '#ffffff',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            padding: '20px',
            marginBottom: '10px',
            borderRadius: '20%',
        }
        const styleGroup = {
            color: '#ffffff',
            borderRadius: 'none',
            display: 'flex',
            flexDirection: 'row',
            alignItems: 'center',
            justifyContent: 'space-around',
            padding: '5px 20px',
            width: '50%',
        }
        const padMe = {
            padding: '0% 30%',
        }
        return (
            <ContainerDiv>
                <Nav />
                <VillagerDiv
                    style={bubbleStyle}
                >

                    <VillagerImg src={villager['image_uri']} alt={villager.name['name-USen']} />
                    <div style={styleGroup}>
                        <h1 style={padMe}>{villager.name['name-USen']}</h1>
                        <h2 style={padMe}><em>{villager.species}</em></h2>
                    </div>
                    <div style={styleGroup}>
                        <h3 style={padMe}>Birthday: {villager['birthday-string']}</h3>
                        <h3 style={padMe}>Personality: {villager.personality}</h3>
                    </div>
                    <div style={styleGroup}>
                        <h3>Catchphrase: '{villager['catch-translations']['catch-USen']}'</h3>
                        <h4>Hobby: {villager.hobby}</h4>
                    </div>
                    <div style={styleLight}>
                        <h4>Quote:</h4>
                        <p>"{villager.saying}"</p>
                    </div>
                </VillagerDiv >
            </ContainerDiv>

        )
    }
}
