import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import styled from 'styled-components'

const StyledForm = styled.form`
    padding: 10px;
    font-size: 1.3em;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content:space-around;
    background-color: #eb0081;
`

const StyledButton = styled.div`
    /* background-color: #00adef; */
    background-image: url('https://i.imgur.com/7vA2Zt8.png');
    background-position: center;
    background-repeat: no-repeat;
    background-size: 80%;
    font-size: 1em;
    height: 70px;
    width: 70px;
    max-height: 150px;
    max-width: 150px;
    margin: 5px;
    color: white;
    border: 1px solid #00adef;
    /* border-radius: 8%; */
    /* padding: 10px; */
    display: flex;
    justify-content: center;
    align-items: center;
    &:hover{
        background-color: #5ce1e6;
    }
`

const StyledInput = styled.input`
    height: 2em;
    width: 75%;
    margin: 5px;
    font-size: 0.9em;
    border: 1px solid white;
`

export default function SearchForm(props) {
    const searchString = props.searchString
    const setSearchString = props.setSearchString
    const [waitSearchString, setWaitSearchString] = useState('')
    const villagers = props.villagers

    function handleChange(e) {
        setWaitSearchString(e.target.value)
    }
    function handleSubmit(e) {
        e.preventDefault()
        setSearchString(waitSearchString)
    }
    return (
        <StyledForm>
            {/* <StyledLabel
                htmlFor="search-input">Find a villager
            </StyledLabel> */}
            <StyledInput
                type="text" value={waitSearchString} placeholder='Search for a villager...' onChange={e => handleChange(e)} id="search-input">

            </StyledInput>
            {/* <Link to="/search" onClick={e => handleSubmit(e)}>Search */}
            <StyledButton
                onClick={e => handleSubmit(e)}>
            </StyledButton>
            {/* </Link> */}
        </StyledForm>

    )
}


