import React, { useEffect, useState } from 'react'
import Nav from './Nav'
import styled, { keyframes, withTheme } from 'styled-components'

const ContainerDiv = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`

const VillagerDiv = styled.div`
margin-top: 20px;
width: 500px;
display: flex;
flex-direction: column;
align-items: center;
`

const VillagerImg = styled.img`
width: 80%;
margin-top: 10%;
`

const LoadingBeachballEffect = keyframes`
     from {
         transform: rotate(0deg)
             }
     to {
         transform: rotate(360deg)
    }
`

const LoadingBeachball = styled.img`
animation: ${LoadingBeachballEffect} 2s infinite;
width: 30%;
height: 30%;
`

const LoadingMessage = styled.div`
display: flex;
align-items: center;
justify-content: center;
`


export default function VillagerDetail(props) {
    if (!props.location.state) {
        return (
            <LoadingMessage>
                <LoadingBeachball src="https://i.imgur.com/lnrCkrf.png" />
            </LoadingMessage>
        )
    }
    else {
        const villager = props.location.state.villager.villager
        let cardColor = villager['bubble-color']
        let bubbleColor = '#ffffff';
        let textColor = cardColor;
        if (cardColor === '#ffffff') {
            bubbleColor = '#000000'
        }
        const cardStyle = {
            backgroundColor: cardColor,
            color: textColor,
        }
        const rowStyle = {
            backgroundColor: bubbleColor,
            display: 'flex',
            flexDirection: 'row',
            justifyContent: 'space-between'
        }

        const elemStyle = {
            padding: '5px'
        }
        return (
            <ContainerDiv>
                <Nav />
                <VillagerDiv
                    style={cardStyle}
                >
                    <VillagerImg src={villager['image_uri']} alt={villager.name['name-USen']} />
                    <div style={rowStyle}>
                        <h1>{villager.name['name-USen']}</h1>
                        <h2><em>{villager.species}</em></h2>
                    </div>
                    <div style={rowStyle}>
                        <h3>Birthday: {villager['birthday-string']}</h3>
                        <h3>Personality: {villager.personality}</h3>
                    </div>
                    <div style={rowStyle}>
                        <h3>Catchphrase: '{villager['catch-translations']['catch-USen']}'</h3>
                        <h4>Hobby: {villager.hobby}</h4>
                    </div>
                    <div style={rowStyle}>
                        <h4>Quote:</h4>
                        <p>"{villager.saying}"</p>
                    </div>
                </VillagerDiv >
            </ContainerDiv>

        )
    }
}
